import React from "react";

const getTodayString = () => {
  const [month, day, year] = new Date()
    .toLocaleDateString("en-US")
    .split("/");
  return `${year}-${month.padStart(2, "0")}-${day.padStart(2, "0")}`;
};

export const AppointmentForm = ({
  contacts,
  title,
  setTitle,
  currentName, 
  setCurrentName,
  contact,
  setContact,
  date,
  setDate,
  time,
  setTime,
  handleSubmit
}) => {

  return (
    <>
     <form onSubmit={handleSubmit}>
      <label>
        Name:
        <input type="text" value={currentName} onChange={(e) => setName(e.target.value)} required />
      </label>
      <br />
      <label>
        Date:
        <input type="date" value={date} onChange={(e) => setDate(e.target.value)} min={getTodayString()} required />
      </label>
      <br />
      <label>
        Time:
        <input type="time" value={time} onChange={(e) => setTime(e.target.value)} required />
      </label>
      <br />
      <label>
        Contact:
        <ContactPicker contacts={contacts} onSelectContact={(contact) => setSelectedContact(contact)} />
      </label>
      <br />
      <button type="submit">Submit</button>
    </form>
    </>
  );
};
