import React, {useState, useEffect} from "react";
import { RouterProvider, createBrowserRouter, createRoutesFromElements, Route, Navigate } from "react-router-dom"
import Root, { ROUTES } from "./components/root/Root";
import { AppointmentsPage } from "./containers/appointmentsPage/AppointmentsPage";
import { ContactsPage } from "./containers/contactsPage/ContactsPage";



function App() {
  /*
  Define state variables for 
  contacts and appointments 
  */
  const [appointments, setAppointments] = useState([
    { 
      name: 'Joe Dirt',
      contact: '405-232-550',
      date: '09/14/99',
      time: '12pm'
    }
  ]);
  const [contacts, setContacts] = useState([]);
  /*
  Implement functions to add data to
  contacts and appointments
  */
  // <input
  //       value={appointments}
  //       onChange={e => setAppointments(e.target.value)}
  //     />
  //     <button onClick={() => {
  //       setAppointments([
  //         ...appointments,
  //         { id: nextId++, name: appointments }
  //       ]);
  //     }}>Add</button>
  //     <ul>
  //       {appointments.map(appointments=> (
  //         <li key={appointments.id}>{appointments.name}</li>
  //       ))}
  //     </ul>

  const handleAddAppointment = (title, contact, date, time) => {

    const appointment = { 
      title,
      contact,
      date,
      time
    }

    setAppointments([...appointments, appointment]);

    // ( appointments.map((appointments) => 

    console.log("New Appointment", title)
  };

  const addContact = (title, contact, date, time) => {
    setContacts( contacts.map((contacts) => [...contacts, 
      { 
        name: title,
        contact: contact,
        date: date,
        time: time
      }]));
  };
  // addNote(newNote) {
  //   this.setState({ toDoNotes: [...this.state.toDoNotes, newNote]})
  // }


  const router = createBrowserRouter(createRoutesFromElements(
    <Route path="/" element={ <Root/> }>
      <Route index element={ <Navigate to={ROUTES.CONTACTS} replace/> }/>
      <Route path={ROUTES.CONTACTS} element={ <ContactsPage  onChange={addContact} /> /* Add props to ContactsPage */ }/>
      <Route path={ROUTES.APPOINTMENTS} element={ <AppointmentsPage  onAddAppointment={handleAddAppointment} /> /* Add props to AppointmentsPage */ }/>
      {}
    </Route>
  ));
  
  return (
    <RouterProvider router={router}/>
  );
}

export default App;
