import React, { useState, useEffect } from "react";

import { ContactForm } from "../../components/contactForm/ContactForm";
import { TileList } from "../../components/tileList/TileList";

export const ContactsPage = (props) => {
  /*
  Define state variables for 
  contact info and duplicate check
  */


  const contacts = props.contacts;
  const addContact = props.addContact;

  const [contactInfo, setContactInfo] = useState({
    name: '',
    phone: '',
    email: '',
  });

  const [name, setName] = useState

  const [isDuplicate, setIsDuplicate] = useState(false);
  
  // const handleContactChange = (event) => {
  //   const { name, value } = event.target;
  //   setContactInfo((prevContactInfo) => ({
  //     ...prevContactInfo,
  //     [name]: value,
  //   }));
  // };



  // useEffect(() => {
  //   const isDuplicateName = contacts.some((contact) => contact.name === contactInfo.name);
  //   setIsDuplicate(isDuplicateName);
  // }, [contactInfo.name, contacts]);
  
  // console.log()

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!isDuplicate) {
      addContact(contactInfo);

      setContactInfo({
      name: '',
      phone: '',
      email: ''
        })
      } 
    };

    
  useEffect(() => {
    const isDuplicateName = () => {
  //   contacts.some((contact) => contact.name === contactInfo.name);
  //   setIsDuplicate(isDuplicateName);
  // }, [contactInfo.name, contacts]);

      const found = contacts.find((contact) => contact.name === contactInfo.name);
      if (found !== undefined) {
        return true;
      }
      return false
    };

    if (isDuplicateName()) {
      setIsDuplicate(true);
    } else {
      setIsDuplicate(false);
    }
  }, [contactInfo.name, contacts, isDuplicate]);
    
    

  /*
  Using hooks, check for contact name in the 
  contacts array variable in props
  */

  return (
    <div>
      <section>
        <h2>Add Contact</h2> 
        <ContactForm 
          name={contactInfo.name}
          setName={setContactInfo.name}
        />

      </section>
      <hr />
      <section>
        <h2>Contacts</h2>
        <TileList tiles={contacts} />
      </section>
    </div>
  );
};
